package com.arcus.javatraining.controlflow;

public class IfExample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int Total = 100;
		int total_number = 10;

		int a333A = 300;

		int myNumber = 33;

		int MyNumber = 33;

		int String;

		int years = 100;

		if (years >= 25 && years < 50) {
			System.out.println(years + " Years" + "Silver Jubilee");
		}
		if (years >= 50 && years < 75) {
			System.out.println(years + " Years" + "Golden Jubilee");
		}
		if (years >= 75 && years < 100) {
			System.out.println(years + " Years" + "Platinum Jubilee");
		}

		else

		if (years == 100) {
			System.out.println(years + " Years" + " Diamond Jubilee");
		} else {
			System.out.println(years + " Undefined Jubilee");
		}

	}

}
